mapper.select:
select a.id, a.NAME, a.subject_id, a.brand_id, a.img_key, a.active_state, (select min(cur_price) from tb_product_sku where product_id = a.id) cur_price, (select min(org_price) from tb_product_sku where product_id = a.id) org_price, a.vice_name, a.create_time, a.seller_id, a.type, a.click_rate, a.storehouse_ids, a.postage_id from tb_product a
AND a.seller_id = #{sellerId,jdbcType=INTEGER}
AND a.id = #{productId,jdbcType=VARCHAR}
AND a.name like CONCAT('%',#{productName},'%')
AND a.active_state = #{activeState,jdbcType=INTEGER}
AND a.subject_id = #{subjectId,jdbcType=INTEGER}
AND a.id IN
#{productIdList[${index}]}
AND a.brand_id = #{brandId,jdbcType=INTEGER}
ORDER BY a.last_update_time DESC
ORDER BY a.create_time DESC
ORDER BY cur_price DESC
ORDER BY cur_price ASC
ORDER BY click_rate DESC

