mapper.select:
select

from tb_product_card

    and product_id = #{productId} and state != 1 and transaction_id = #{transactionId}

mapper.update:
UPDATE tb_product_card
SET transaction_id = #{transactionId}, state = 2, buyer_id = #{buyerId}, sold_time = CURRENT_TIMESTAMP
WHERE state = 1 and product_id = #{productId}
ORDER BY create_time asc
limit #{num}

update tb_product_card
SET transaction_id = '', state = 1, buyer_id = 0, sold_time = null
WHERE transaction_id = #{transactionId} and product_id = #{productId}

mapper.select:
select
count(*)
from tb_product_card

    and product_id = #{productId} and seller_id = #{sellerId}

        and card_number like concat('%',#{cardNumber} ,'%')


        and state = #{state}

select

from tb_product_card

    and product_id = #{productId,jdbcType=VARCHAR} and seller_id = #{sellerId}

        and card_number like concat('%',#{cardNumber} ,'%')


        and state = #{state}


        and sku_num = #{skuNum}


order by id
limit #{start}, #{count}

SELECT p.product_id id, p.sku_num, MAX(t.create_time) create_time, COUNT(t.id) total, SUM( CASE WHEN t.state = 1 THEN 1 ELSE 0 END) AS unused FROM tb_product_sku p LEFT JOIN tb_product_card t ON t.product_id = p.product_id AND t.`sku_num` = p.`sku_num`
AND p.product_id = #{productId} and p.storehouse_id != 0
GROUP BY p.product_id, p.sku_num ORDER BY create_time asc LIMIT #{start}, #{count}

SELECT COUNT(*) FROM ( SELECT p.id, p.name, COALESCE(sum(s.sold_num),0) sold_num, COALESCE(sum(s.checked_num),0) checked_num FROM tb_product p, tb_product_card_statistics s
AND s.product_id = p.id AND s.seller_id = #{sellerId}
GROUP BY p.id ) t

SELECT p.id, p.name, COALESCE(sum(s.sold_num),0) sold_num, COALESCE(sum(s.checked_num),0) checked_num FROM tb_product p, tb_product_card_statistics s
AND s.product_id = p.id AND s.seller_id = #{sellerId}
GROUP BY p.id ORDER BY checked_num desc, sold_num desc LIMIT #{start}, #{count}

SELECT
  s.`statistics_date`,
  COALESCE(SUM(s.checked_num), 0) checked_num
FROM
  tb_product_card_statistics s
WHERE s.`seller_id` = #{sellerId}
  AND LEFT(s.`statistics_date`, 7) = #{month}
  GROUP BY s.`statistics_date`
  ORDER BY s.`statistics_date` DESC
  LIMIT #{start}, #{count}

select

    COALESCE(sum(s.sold_num),0)


    COALESCE(sum(s.checked_num),0)


from tb_product p, tb_product_card_statistics s

    and p.id = s.product_id and s.seller_id = #{sellerId}

      and s.product_id = #{productId}


      and s.statistics_date = #{day}


      and LEFT(s.`statistics_date`,7) = #{month}

SELECT c.* FROM tb_product p, tb_product_card c
AND c.product_id = p.id AND c.seller_id = #{sellerId} and c.product_id = #{productId} and c.state = 3 and DATE_FORMAT(c.checked_time,'%Y-%m') = #{month}
order by c.checked_time DESC LIMIT #{start}, #{count}
